import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';

const HeaderMenuItem: React.FC<{
  to: string;
  title: string;
  active?: boolean;
  onClick?(target: string): void;
}> = (props) => {
  return (
    <li
      className={`cursor-pointer flex items-center h-full p-4 hover:bg-opacity-25 transition-all duration-1000 ${
        props.active === true ? 'font-medium text-primary bg-highlight hover:bg-opacity-75' : 'hover:bg-black'
      }`}
      onClick={() => {
        props.onClick?.(props.to);
      }}
    >
      {props.title}
    </li>
  );
};

const HeaderMenu: React.FC = (props) => {
  return (
    <nav>
      <ul className="flex h-full">{props.children}</ul>
    </nav>
  );
};

const Header: React.FC = () => {
  const [menuSelected, setMenuSelected] = useState<string | undefined>('welcome');

  useEffect(() => {
    if (menuSelected !== undefined) {
      document.getElementById(menuSelected)?.scrollIntoView();
    }
  }, [menuSelected]);

  return (
    <header className="bg-black bg-opacity-25 w-full h-16 flex justify-center text-white fixed">
      <HeaderMenu>
        <HeaderMenuItem to="welcome" title="Welcome" active={menuSelected === 'welcome'} onClick={setMenuSelected} />
        <HeaderMenuItem to="about" title="About" active={menuSelected === 'about'} onClick={setMenuSelected} />
        <HeaderMenuItem to="contact" title="Contact" active={menuSelected === 'contact'} onClick={setMenuSelected} />
      </HeaderMenu>
    </header>
  );
};

const Box: React.FC<{ className?: string }> = (props) => {
  return (
    <div
      className={`w-24 text-center shadow bg-black bg-opacity-25 p-4 rounded-sm flex flex-col items-center ${
        props.className ?? ''
      }`}
    >
      {props.children}
    </div>
  );
};

